            this.getInput1(), this.getInput2(), this.getInput3(), this.getInput4()
        ])
    }

    getAvailableInput(material) {
        return(this.getInputMax(material) - this.getInputAmount(material))
    }

    addInput(material, amount) {
        console.log(this.getInput())
        for (let input of this.getInput()) {
            if(input.name === material) {
                console.log('before addInput: ' + input.amount)
                input.amount = input.amount + amount//Math.min(input.amount + _amount, this.getInputMax(material))
                console.log('after addInput: ' + input.amount)
                return(input.amount)
            }
        }
    }

    removeInput(material, amount) {
        for (let input of this.getInput()) {
            console.log("input name: " + input.name)
            console.log("material: " + material)
            if(input.name === material) {
                console.log('before2: ' + input.amount)
                input.amount = Math.max(input.amount - amount, 0)
                console.log('after2: ' + input.amount)
                return(input.amount)
            }
        }
    }

    addOutput(_name, _amount) {
        this.output = {
            name: _name,
            amount: _amount
        }
    }

    getInputAmount(material) {
        for (let input of this.getInput()) {
            if(input.name === material) { return (input.amount) }
        }
        return(0)
    }

    getInputMax(material) {
        for (let input of this.getInput()) {
            if(input.name === material) { return(input.max) } 
        }
        return(0)
    }


    // edit this so that it correctly splits the output 
    // and records how much of what is sent to which child
    sendOutput() { 
        let material = this.getOutput().name
        let children = this.getChildren()
        let effected = [];
        let prevConsumption = 0;
        let consumption = 0; //total amount of output consumed by children

        for (let child of children) { 
            consumption += child.getAvailableInput(material)
        }

        console.log("material: " + material)
        console.log("consumption: " + consumption)

        this.splitOutput(this.getOutput(), children)

        // let remainingOutput = this.getOutput().amount
        // if (consumption >= this.getOutput().amount) { //if consumption > output, split evenly
        //     let split = Math.round(this.getOutput().amount / children.length)
        //     for (let child of children) {
        //         child.addInput(
        //             material, 
        //             Math.min(split + child.getInputAmount(material), child.getInputMax(material))
        //         )
        //     }
        // } 
        
        // else { //split so that each child gets their max input
        //     for (let child of children) {
        //         child.addInput(material, child.getInputMax(material))
        //     }
        // }

    }

    wipeOutputToChildren(output, children) {
        if(children.length <= 1){ return }
        console.log('wiped')
        let split = Math.round( output.amount / Math.max( children.length - 1, 1 ) )
        for (let child of children) {
            child.removeInput(output.name, split)
        }
    }

    splitOutput(output, children) {
        let material = output.name
        let amount = output.amount
        this.wipeOutputToChildren(output, children)
        let countAtMax = 0;

        //if at full capacity don't count in the split
        for(let child of children) {
            if(child.getInputAmount(material) === child.getInputMax(material)) { countAtMax += 1 }
        }

        let split = Math.round(amount / (children.length - countAtMax) )

        for (let child of children) {
            child.addInput(
                material,
                split
            )
            amount -= split
        }

        if(amount > 0) {
            this.splitOutput(material, amount, children)
        }
    }

    addChild(_child) { //sets a child node and adds parent
        this.children.push(_child);
    }
    
    removeChild(_child) {
        for (let child of this.getChildren()) {
            if (_child === child) {
                this.children = this.getChildren().filter( (c) => {
                    return (c !== _child)
                })
            }
        }
    }

    wipeInput() {
        this.input = [];
    }

    wipeInputOutput() {
        this.input = []; //[{name: a, amount:x}, ...]
        this.output = {};
    }

    wipeChildrenInput() {
        for(let child of this.children) {
            child.wipeInput()
        }
    }

    wipeChildren() {
        this.children = [];
    }

    wipeAll() {
        this.children = [];
        this.maxInput = [];
        this.input = []; //[{name: a, amount:x}, ...]
        this.output = {};
    }

}
